
// 当纯HTML被完全加载以及解析时，DOMContentLoaded 事件会被触发，而不必等待样式表，图片或者子框架完成加载。
window.addEventListener('DOMContentLoaded', function () {
    if (window.matchMedia("(max-width: 860px)").matches) {
        var styleNode = document.createElement("style");
        styleNode.innerHTML = "body.dark{background-image: url('https://gitee.com/yl3021027861/demo/raw/master/img/a7psk-xj56y.webp') !important}";
        document.head.appendChild(styleNode);
    } else {
        var styleNode = document.createElement("style");
        styleNode.innerHTML = "body.dark{background-image: url('https://gitee.com/yl3021027861/demo/raw/master/img/bg3.webp') !important}";
        document.head.appendChild(styleNode);
    }
// 添加当用户调整窗口大小时触发的事件监听器
    window.addEventListener('resize', function () {
        if (window.matchMedia("(max-width: 860px)").matches) {
            var styleNode = document.createElement("style");
            styleNode.innerHTML = "body.dark{background-image: url('https://gitee.com/yl3021027861/demo/raw/master/img/a7psk-xj56y.webp') !important}";
            document.head.appendChild(styleNode);
        } else {
            var styleNode = document.createElement("style");
            styleNode.innerHTML = "body.dark{background-image: url('https://gitee.com/yl3021027861/demo/raw/master/img/bg3.webp') !important}";
            document.head.appendChild(styleNode);
        }
    })

})



